import React, { Component } from 'react';

import Ad from '../../Components/Ad/Ad';
import NewAdd from '../../Components/NewAdd/NewAdd';
import './Main.css';
import axios from 'axios';

class Main extends Component {
  state = {
    adds:[],
    selectedPostId: null,
    error: false
  }

  componentDidMount (){
    axios.get('https://jsonplaceholder.typicode.com/posts')
    .then(response=>{
      const adds = response.data.slice(0,4);
      this.setState({adds:adds});
    })
    .catch(error => {
      console.log(error);
      this.setState({error: true});
    });
  }

  postSelectedHandler = (id)=>{
    this.setState({selectedPostId:id});
  }

    render () {
      let adds = <p style={{textAlign:'center'}}>Something went wrong</p>;
        if(!this.state.error){
          adds = this.state.adds.map(
            add=>{
              return <Add key={add.id} title={add.title} author={add.userId} clicked={() => this.postSelectedHandler(add.id)} />;
            }
          );
        }

        return (
            <div>
                <section className="Adds">
                    {adds}
                </section>
                <section>
                    {/*<FullPost id={this.state.selectedPostId} /> */}
                </section>
                <section>
                    <NewAdd />
                </section>
            </div>
        );
    }
}

export default Main;
