import React, { Component } from 'react';

import Ad from '../../Components/Ad/Ad';
import NewAd from '../../Components/NewAd/NewAd';
import './Main.css';
import axios from 'axios';

class Main extends Component {
  state = {
    ads:[],
    selectedAdId: null,
    error: false
  }

  componentDidMount (){
    axios.get('https://jsonplaceholder.typicode.com/posts')
    .then(response=>{
      const ads = response.data.slice(0,4);
      this.setState({ads:ads});
    })
    .catch(error => {
      console.log(error);
      this.setState({error: true});
    });
  }

  adSelectedHandler = (id)=>{
    this.setState({selectedAdId:id});
  }

    render () {
      let ads = <p style={{textAlign:'center'}}>Something went wrong</p>;
        if(!this.state.error){
          ads = this.state.ads.map(
            ad=>{
              return <Ad key={ad.id} title={ad.title} author={ad.userId} clicked={() => this.adSelectedHandler(ad.id)} />;
            }
          );
        }

        return (
            <div>
                <section className="Ads">
                    {ads}
                </section>
                <section>
                    {/*<FullPost id={this.state.selectedAdId} /> */}
                </section>
                <section>
                    <NewAd />
                </section>
            </div>
        );
    }
}

export default Main;
